import numpy as npfrom PIL import Imagewidth = 5height = 4#creating a 3D array where each element is a pixel and each pixel has 3 values#ie rgb values and the data type is unsigned intarray = np.zeros([height, width, 3], dtype = np.uint8)#forming the imageimg = Image.fromarray(array)img.save('test.png')array1 = np.zeros([100, 200, 3], dtype = np.uint8)array1[:, :100] = [255, 128, 0]#orange colorarray1[:, 100:] = [0, 0, 255]#blue colorimg = Image.fromarray(array1)img.save('test1.png')